+  // YouTube動画IDをURLから取得
+  function getVideoId() {
+    const url = new URL(location.href);
+    return url.searchParams.get('v');
+  }
+
+  // YouTube Data APIでコメントを全件取得（最大1000件まで）
+  async function fetchComments(videoId) {
+    if (!videoId) return { count: 0, comments: [] };
+    const maxTotal = 1000;
+    let comments = [];
+    let pageToken = '';
+    try {
+      while (comments.length < maxTotal) {
+        let apiUrl = `https://www.googleapis.com/youtube/v3/commentThreads?part=snippet&videoId=${videoId}&key=${YOUTUBE_API_KEY}&maxResults=100`;
+        if (pageToken) apiUrl += `&pageToken=${pageToken}`;
+        const res = await fetch(apiUrl);
+        const data = await res.json();
+        if (data.error) {
+          console.error('YouTube API Error:', data.error);
+          break;
+        }
+        const newComments = data.items ? data.items.map(item => ({
+          author: item.snippet.topLevelComment.snippet.authorDisplayName,
+          authorProfileImageUrl: item.snippet.topLevelComment.snippet.authorProfileImageUrl,
+          authorChannelUrl: item.snippet.topLevelComment.snippet.authorChannelUrl || '#',
+          text: item.snippet.topLevelComment.snippet.textDisplay,
+          publishedAt: item.snippet.topLevelComment.snippet.publishedAt,
+          updatedAt: item.snippet.topLevelComment.snippet.updatedAt,
+          likeCount: item.snippet.topLevelComment.snippet.likeCount || 0,
+          id: item.snippet.topLevelComment.id
+        })) : [];
+        comments = comments.concat(newComments);
+        if (!data.nextPageToken || comments.length >= maxTotal) break;
+        pageToken = data.nextPageToken;
+      }
+      return { count: comments.length, comments: comments };
+    } catch (e) {
+      console.error('API fetch error:', e);
+      return { count: 0, comments: [] };
+    }
+  }
+
+  // YouTubeの「○分前」形式
+  function formatTimeAgo(publishedAt) {
+    const now = new Date();
+    const published = new Date(publishedAt);
+    const diffMs = now - published;
+    const minutes = Math.floor(diffMs / (1000 * 60));
+    const hours = Math.floor(diffMs / (1000 * 60 * 60));
+    const days = Math.floor(diffMs / (1000 * 60 * 60 * 24));
+    const weeks = Math.floor(days / 7);
+    const months = Math.floor(days / 30);
+    const years = Math.floor(days / 365);
+    if (years > 0) {
+      return `${years} 年前`;
+    } else if (months > 0) {
+      return `${months} か月前`;
+    } else if (weeks > 0) {
+      return `${weeks} 週間前`;
+    } else if (days > 0) {
+      return `${days} 日前`;
+    } else if (hours > 0) {
+      return `${hours} 時間前`;
+    } else if (minutes > 0) {
+      return `${minutes} 分前`;
+    } else {
+      return '今';
+    }
+  }
+
+  // 画面上の全コメントIDをできるだけ正確に取得する（デバッグ版）
+  function getDisplayedCommentIds() {
+    const ids = new Set();
+    const debugInfo = [];
+    
+    // 1. 通常コメント（メインの方法）
+    document.querySelectorAll('ytd-comment-thread-renderer').forEach((threadRenderer, index) => {
+      // コメント本文のリンクからIDを取得
+      const linkWithLc = threadRenderer.querySelector('a[href*="lc="]');
+      if (linkWithLc) {
+        const match = linkWithLc.href.match(/[?&]lc=([a-zA-Z0-9_-]+)/);
+        if (match) {
+          ids.add(match[1]);
+          debugInfo.push(`方法1-${index}: ${match[1]} (${threadRenderer.querySelector('#content-text')?.textContent?.slice(0, 30) || 'テキスト不明'}...)`);
+        }
+      }
+      
+      // data属性系も確認
+      const dataId = threadRenderer.getAttribute('data-id');
+      if (dataId) {
+        ids.add(dataId);
+        debugInfo.push(`data-id-${index}: ${dataId}`);
+      }
+    });
+
+    // 2. 返信コメント
+    document.querySelectorAll('ytd-comment-renderer').forEach((commentRenderer, index) => {
+      const linkWithLc = commentRenderer.querySelector('a[href*="lc="]');
+      if (linkWithLc) {
+        const match = linkWithLc.href.match(/[?&]lc=([a-zA-Z0-9_-]+)/);
+        if (match) {
+          ids.add(match[1]);
+          debugInfo.push(`返信${index}: ${match[1]}`);
+        }
+      }
+    });
+
+    // 3. 既に挿入した未表示コメント
+    document.querySelectorAll('[data-ytcmtck-hidden="true"]').forEach((hiddenComment, index) => {
+      const commentId = hiddenComment.getAttribute('data-comment-id');
+      if (commentId) {
+        ids.add(commentId);
+        debugInfo.push(`既存未表示${index}: ${commentId}`);
+      }
+    });
+
+    // デバッグ情報をログに出力
+    addLog(`=== デバッグ: 画面上で検出したコメントID一覧 ===`, 'debug');
+    debugInfo.forEach(info => addLog(info, 'debug'));
+    addLog(`=== 合計: ${ids.size}個のユニークID ===`, 'debug');
+    
+    return ids;
+  }
